mapper.select:
WITH T_CODE AS ( SELECT B.SURVSEQ, B.SURVKINDCD, B.CDTYPE, B.CD, B.CDNM FROM TCCM_CODE B WHERE SURVSEQ = #{SURVSEQ} AND SURVKINDCD = #{SURVKINDCD} AND B.CD != 'GROUPCD' AND B.CDTYPE IN ('1001','1004','0527','0529','2012','6007','2011','2001') ),BASE AS ( SELECT T1.SURVSEQ,T1.SURVKINDCD,T1.SCHKINDCD,T1.SCHCD,T5.SCHNM,T1.ORGCD,T1.COLCD,T1.DEPTCD,T1.DEPTNM,T1.COLNM ,T1.MOECLASS,T1.DNCD,T1.DETAILDEPTNM,T1.DEGREECD,T1.INYM,T1.OUTYM,T1.EXCHANGEYN,T1.STUDENTNO ,T1.UNIQUEKEY,T1.BIRTHYEAR,T1.SEXCD,T1.AGE,T1.JOBTYPECD ,T1.WELFARECD,T1.CLERICYN,T1.STARTDT,T1.ENDDT,T1.SCHWORKFORM,T1.WORKHOUR,T1.NACODE ,T1.FORGCOMNM,T1.OVERSEAVISATYPE,T1.OVERSEAVISAYN,T1.OVERSEAWORKTYPE,T1.SAMEUNIVYN ,T1.GOSCHCD,T6.SCHNM AS GOSCHNM,T1.GOORGCD,T1.GOFORGSCHNM,T7.JOBTYPEGROUPCD,T7.JOBTYPEGROUPNM ,T1.ARTNM,T1.ARTPL,T1.ARTPO,T1.ALDYEMPLYN,T1.TRANSCLOSEYN,T1.MNTNEMPLYN1,T1.MNTNEMPLYN2 ,T1.MNTNEMPLYN3,T1.MNTNEMPLYN4,T1.TARGETYN,T1.SALARY,T1.INDLGROUPCD,T1.COMTYPE,T1.COMSIZE,T1.WORKERID, SP_DEC_FU(T8.NAME) AS NAME ,T2.JOBTYPECD AS JOBTYPECD_BF, DECODE(T1.ALDYEMPLYN , '1', '예', '2', '아니오') AS ALDYEMPLNM FROM (SELECT SURVSEQ, SURVKINDCD, SCHKINDCD, SCHCD, ORGCD, COLCD, DEPTCD, DEPTNM, COLNM , MOECLASS, DNCD, DETAILDEPTNM, DEGREECD, INYM, OUTYM, EXCHANGEYN, STUDENTNO , UNIQUEKEY, BIRTHYEAR, SEXCD, AGE , (CASE WHEN JOBTYPECD NOT IN ('61','62','63','64','65') AND NVL(EXCHANGEYN,2) != '1' AND WELFARECD != '9' THEN '66' ELSE JOBTYPECD END) JOBTYPECD , WELFARECD, CLERICYN, STARTDT, ENDDT, SCHWORKFORM, WORKHOUR, NACODE , FORGCOMNM, OVERSEAVISATYPE, OVERSEAVISAYN, OVERSEAWORKTYPE, SAMEUNIVYN , GOSCHCD, GOORGCD, GOFORGSCHNM, ARTNM, ARTPL, ARTPO, ALDYEMPLYN, TRANSCLOSEYN, MNTNEMPLYN1, MNTNEMPLYN2 , MNTNEMPLYN3, MNTNEMPLYN4, TARGETYN, SALARY, INDLGROUPCD, COMTYPE, COMSIZE, WORKERID FROM TMAG_ATRGRDRST WHERE SURVSEQ = #{SURVSEQ} AND SURVKINDCD = #{SURVKINDCD} AND SCHCD = #{SCHCD} AND ORGCD = #{ORGCD} ) T1 , TMAG_ATRGRDINFO T2, TMCM_GOSCHOOL T6, TMAD_SCHOOL T5,TCCM_JOBCODE T7, TMGP_GRDPSNINFO T8 WHERE 1=1 AND T1.SURVSEQ = T2.SURVSEQ AND T1.SURVKINDCD = T2.SURVKINDCD AND T1.SCHCD = T2.SCHCD AND T1.ORGCD = T2.ORGCD AND T1.UNIQUEKEY = T2.UNIQUEKEY AND T1.SURVSEQ = T6.SURVSEQ(+) AND T1.SURVKINDCD = T6.SURVKINDCD(+) AND T1.GOSCHCD = T6.SCHCD(+) AND T1.GOORGCD = T6.ORGCD(+) AND T1.SURVSEQ = T5.SURVSEQ AND T1.SURVKINDCD = T5.SURVKINDCD AND T1.SCHCD = T5.SCHCD AND T1.ORGCD = T5.ORGCD AND T1.SURVSEQ = T7.SURVSEQ(+) AND T1.SURVKINDCD = T7.SURVKINDCD(+) AND DECODE(T1.JOBTYPECD,'52','51','4','80','14','81','16','82', T1.JOBTYPECD) = T7.JOBTYPECD(+) AND T1.SURVSEQ = T8.SURVSEQ(+) AND 'AG' = T8.SURVKINDCD(+) AND T1.SCHCD = T8.SCHCD(+) AND T1.ORGCD = T8.ORGCD(+) AND T1.STUDENTNO = T8.STUDENTNO(+)
AND T1.DEPTCD = #{DEPTCD}
AND T1.DNCD = #{DNCD}
AND T1.STUDENTNO = #{STUDENTNO}
AND T7.JOBTYPEGROUPCD = #{JOBTYPEGROUPCD}
AND T8.NAME = SP_ENC_FU(#{NAME})
AND T1.UNIQUEKEY = #{UNIQUEKEY}
) SELECT FNL.* FROM ( SELECT ROWNUM AS RNUM, F1.* FROM( SELECT A.* , (case when substr(A.DEGREECD,0,1)='1' then '석사' when substr(A.DEGREECD,0,1)='2' then '박사' else '' end) DEGREENM , B1.CDNM AS ORGNM , B2.CDNM AS SCHKINDNM , B4.CDNM AS DNNM , B5.CDNM AS SEXNM , B6.CDNM AS SCHWORKFORMNM , B7.CDNM AS NACODENM , B8.CDNM AS OVERSEAWORKTYPENM , B9.CDNM AS GOORGNM , B10.CDNM AS JOBTYPENM_BF , DECODE(A.JOBTYPEGROUPCD, '7', A.JOBTYPEGROUPNM, B11.CDNM) AS JOBTYPENM FROM BASE A, T_CODE B1, T_CODE B2, T_CODE B4, T_CODE B5, T_CODE B6, T_CODE B7, T_CODE B8, T_CODE B9, T_CODE B10, T_CODE B11 WHERE '1001' = B1.CDTYPE(+) AND A.ORGCD = B1.CD(+) AND '0527' = B2.CDTYPE(+) AND A.SCHKINDCD = B2.CD(+) AND '1004' = B4.CDTYPE(+) AND A.DNCD = B4.CD(+) AND '0529' = B5.CDTYPE(+) AND A.SEXCD = B5.CD(+) AND '2012' = B6.CDTYPE(+) AND A.SCHWORKFORM = B6.CD(+) AND '6007' = B7.CDTYPE(+) AND A.NACODE = B7.CD(+) AND '2011' = B8.CDTYPE(+) AND A.OVERSEAWORKTYPE = B8.CD(+) AND '1001' = B9.CDTYPE(+) AND A.GOORGCD = B9.CD(+) AND '2001' = B10.CDTYPE(+) AND A.JOBTYPECD_BF = B10.CD(+) AND '2001' = B11.CDTYPE(+) AND A.JOBTYPECD = B11.CD(+) ORDER BY A.COLNM,A.DEPTNM,A.DETAILDEPTNM,A.DNCD,A.NAME ) F1 ) FNL WHERE RNUM BETWEEN #{START} AND #{END}

SELECT COUNT(*) TOTALPAGECNT FROM (SELECT SURVSEQ, SURVKINDCD, SCHKINDCD, SCHCD, ORGCD, COLCD, DEPTCD, DEPTNM, COLNM , MOECLASS, DNCD, DETAILDEPTNM, DEGREECD, INYM, OUTYM, EXCHANGEYN, STUDENTNO , UNIQUEKEY, BIRTHYEAR, SEXCD, AGE , (CASE WHEN JOBTYPECD NOT IN ('61','62','63','64','65') AND NVL(EXCHANGEYN,2) != '1' AND WELFARECD != '9' THEN '66' ELSE JOBTYPECD END) JOBTYPECD , WELFARECD, CLERICYN, STARTDT, ENDDT, SCHWORKFORM, WORKHOUR, NACODE , FORGCOMNM, OVERSEAVISATYPE, OVERSEAVISAYN, OVERSEAWORKTYPE, SAMEUNIVYN , GOSCHCD, GOORGCD, GOFORGSCHNM, ARTNM, ARTPL, ARTPO, ALDYEMPLYN, TRANSCLOSEYN, MNTNEMPLYN1, MNTNEMPLYN2 , MNTNEMPLYN3, MNTNEMPLYN4, TARGETYN, SALARY, INDLGROUPCD, COMTYPE, COMSIZE, WORKERID FROM TMAG_ATRGRDRST WHERE SURVSEQ = #{SURVSEQ} AND SURVKINDCD = #{SURVKINDCD} AND SCHCD = #{SCHCD} AND ORGCD = #{ORGCD} ) T1 , TMAG_ATRGRDINFO T2, TMCM_GOSCHOOL T6, TMAD_SCHOOL T5,TCCM_JOBCODE T7, TMGP_GRDPSNINFO T8 WHERE 1=1 AND T1.SURVSEQ = T2.SURVSEQ AND T1.SURVKINDCD = T2.SURVKINDCD AND T1.SCHCD = T2.SCHCD AND T1.ORGCD = T2.ORGCD AND T1.UNIQUEKEY = T2.UNIQUEKEY AND T1.SURVSEQ = T6.SURVSEQ(+) AND T1.SURVKINDCD = T6.SURVKINDCD(+) AND T1.GOSCHCD = T6.SCHCD(+) AND T1.GOORGCD = T6.ORGCD(+) AND T1.SURVSEQ = T5.SURVSEQ AND T1.SURVKINDCD = T5.SURVKINDCD AND T1.SCHCD = T5.SCHCD AND T1.ORGCD = T5.ORGCD AND T1.SURVSEQ = T7.SURVSEQ(+) AND T1.SURVKINDCD = T7.SURVKINDCD(+) AND DECODE(T1.JOBTYPECD,'52','51','4','80','14','81','16','82', T1.JOBTYPECD) = T7.JOBTYPECD(+) AND T1.SURVSEQ = T8.SURVSEQ(+) AND 'GP' = T8.SURVKINDCD(+) AND T1.SCHCD = T8.SCHCD(+) AND T1.ORGCD = T8.ORGCD(+) AND T1.STUDENTNO = T8.STUDENTNO(+)
AND T1.DEPTCD = #{DEPTCD}
AND T1.DNCD = #{DNCD}
AND T1.STUDENTNO = #{STUDENTNO}
AND T7.JOBTYPEGROUPCD = #{JOBTYPEGROUPCD}
AND T8.NAME = SP_ENC_FU(#{NAME})
AND T1.UNIQUEKEY = #{UNIQUEKEY}

SELECT 
	T1.DEPTCD
	,T3.DEPTNM
FROM TMAG_ATRGRDINFO T1, TMCM_DEPT T3
WHERE
 	T1.SURVSEQ = #{SURVSEQ}
	AND T1.SURVKINDCD = #{SURVKINDCD}
	AND T1.SCHCD = #{SCHCD}
	AND T1.ORGCD = #{ORGCD}	
	AND T1.SURVSEQ = T3.SURVSEQ
	AND T1.SURVKINDCD = T3.SURVKINDCD
	AND T1.DEPTCD = T3.DEPTCD
GROUP BY T1.DEPTCD,T3.DEPTNM

SELECT 
	T1.DNCD, T2.CDNM
FROM TMAG_ATRGRDINFO T1, TCCM_CODE T2
WHERE
 	T1.SURVSEQ = #{SURVSEQ}
	AND T1.SURVKINDCD = #{SURVKINDCD}
	AND T1.SCHCD = #{SCHCD}
	AND T1.ORGCD = #{ORGCD}
	AND T1.SURVSEQ = T2.SURVSEQ
	AND T1.SURVKINDCD = T2.SURVKINDCD
	AND T2.CDTYPE = '1004'
	AND T1.DNCD = T2.CD
GROUP BY T1.DNCD, T2.CDNM
