mapper.select:
SELECT COUNT(*) TOTALPAGECNT FROM
TMAG_ATRGRDRST T1
TMGP_GRDPSNVIEW T1
, TMCM_GOSCHOOL T6
, TMAD_SCHOOL T5 , TCCM_JOBCODE T7
, TMAG_ATRGRDINFO T8
, TMgp_grdpsnINFO T8
WHERE T1.SURVSEQ = #{SURVSEQ} AND T1.SURVKINDCD = #{SURVKINDCD} AND T1.SCHCD = #{SCHCD} AND T1.ORGCD = #{ORGCD}
AND T1.SURVSEQ = T6.SURVSEQ(+) AND T1.SURVKINDCD = T6.SURVKINDCD(+) AND T1.GOSCHCD = T6.SCHCD(+) AND T1.GOORGCD = T6.ORGCD(+)
AND T1.SURVSEQ = T5.SURVSEQ AND T1.SURVKINDCD = T5.SURVKINDCD AND T1.SCHCD = T5.SCHCD AND T1.ORGCD = T5.ORGCD AND T1.SURVSEQ = T7.SURVSEQ(+) AND T1.SURVKINDCD = T7.SURVKINDCD(+) AND DECODE(T1.JOBTYPECD,'52','51','4','80','14','81','16','82', T1.JOBTYPECD) = T7.JOBTYPECD(+) AND T1.SURVSEQ = T8.SURVSEQ AND T1.SURVKINDCD = T8.SURVKINDCD AND T1.SCHCD = T8.SCHCD AND T1.ORGCD = T8.ORGCD AND T1.UNIQUEKEY = T8.UNIQUEKEY
AND T1.DEPTCD = #{DEPTCD}
AND T1.DNCD = #{DNCD}
AND T1.STUDENTNO = #{STUDENTNO}
AND (T7.JOBTYPEGROUPCD is null or T7.JOBTYPEGROUPCD = '')
AND T7.JOBTYPEGROUPCD = #{JOBTYPEGROUPCD}
AND T1.UNIQUEKEY = #{UNIQUEKEY}

